import React from "react";
import ReactDOM from "react-dom/client";
import "./App.css";

function App() {
    return (
        <div className="container">
            <Header />
            <Menu />
            <Footer/>
        </div>
    )
}

function Header() {
    return <h1 style={{color:"orange", fontSize: "48px", textTransform: "uppercase"}}>thaqif's pizza co.</h1>
}

function Pizza() {
    return (
        <div className="pizza">
            <img src="spinaci.jpg" alt="Pizza Spinach" />
            <h2>Spinach Pizza</h2>
            <p>Tomato, mozarella, spinach, and ricotta cheese</p>
            <p>10</p>
        </div>
    )
}

function Menu() {
    return (
        <div className="menu">
        <h2>Our Menu</h2>
        <Pizza />
        <Pizza />
    </div> 
    )
}

function Footer() {
    const hour = new Date().getHours();
    const isOpen = hour > 8 && hour < 22? true: false;
    return (
    <div>
        <footer>
            <p>
                { isOpen ? "We're currently open" : "Sorry we're closed" }
            </p>
        </footer>
    </div>  
    )
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App />)
