import React from "react";
import ReactDOM from "react-dom/client";
import "./index.css";

const pizza = 
[
    {pizza:"Margherita Pizza", ingredient:"Dough, Basil, Tomatoes, Olive Oil, Garlic, Mozarella", price:1.00}, 
    {pizza:"Salamino Pizza", ingredient:"Dough, Salami, Grape Tomatoes, Marinara Sauce, Red Onion, Parmesan", price:1.50},
    {pizza:"Prosciutto", ingredient:"Dough, Tomatoes, Mozarella", price:1.20}, 
    {pizza:"Spinaci", ingredient:"Dough, Bell Pepper, Tomatoes, Mozarella", price:1.10}, 
]

const listPizza = pizza.map((pizza, i) => (
    <Pizza name={pizza.name} ingredient={pizza.ingredient} price={pizza.price}/>
))

function App() {
    return (
        <div className="container">
            <Header />
            <Menu />
            <Footer/>
            {listPizza}
        </div>
    )
}

function Header() {
    return <h1 style={{color:"orange", fontSize: "48px", textTransform: "uppercase"}}>thaqif's pizza co.</h1>
}

function Pizza(prop) {
    return (
        <div className="pizza">
            <img src={prop.image} alt={prop.imageName} />
            <h2>{prop.name}</h2>
            <p>{prop.ingredient}</p>
            <p>${prop.price}</p>
        </div>
    )
}

Pizza.defaultProps = {
    imageName: "No Image",
    name: "Pizza",
    ingredient: "Dough, Sauce, Mozarella",
    price: 0.01
}

function Menu() {
    return (
        <div className="menu">
        <h2>Our Menu</h2>
    </div> 
    )
}

function Footer() {
    const hour = new Date().getHours();
    const isOpen = hour >= 10 && hour <= 22? true: false;
    return (
    <div>
        <footer>
            <p>
                { isOpen ? "We're currently open" : "Sorry we're closed" }
            </p>
        </footer>
    </div>  
    )
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App />)
